def normalize(kv: dict, prefix: list = None) -> dict:
    """
    Used to write json to the same grain
    :param kv:
    :param prefix:
    :return:
    """
    prefix = prefix or []
    for k, v in kv.items():
        if isinstance(v, dict):
            yield from normalize(v, prefix + [str(k)])
        else:
            if prefix:
                yield '_'.join(prefix + [str(k)]), v
            else:
                yield str(k), v


def flatten(d):
    out = {}
    for key, val in d.items():
        if isinstance(val, dict):
            val = [val]
        if isinstance(val, list):
            for subdict in val:
                deeper = flatten(subdict).items()
                out.update({key + '_' + key2: val2 for key2, val2 in deeper})
        else:
            out[key] = val
    return out


